<script setup>
const selectedTopic = ref("Adventure");
const currentUser = useState("currentUser")


const chooseTopic = (topic) => {
  selectedTopic.value =
    topic.charAt(0) + topic.split(" ")[0].slice(1).toLowerCase();
};

onBeforeMount(() => {
  if (!localStorage.getItem("currentUser")) {
    navigateTo("/login");
    return;
  }
})

</script>

<template>
  <div>
    <Header @chooseTopic="chooseTopic" />
    <div class="container">
      <PeopleList :selectedTopic="selectedTopic" v-if="currentUser"/>
      
    </div>
  </div>
</template>

